def longestPalindrome(s):
    """
    :type s: str
    :rtype: int
    """
    from collections import Counter
    alpha_count = Counter(s)
    max_length = 0
    odd = False
    for count in alpha_count.values():
        if count % 2 == 0:
            max_length += count
        else:
            max_length += count - 1
            odd = True
    return max_length + 1 if odd else max_length


